import { toggleMachine } from "./scripts/state"
import { interpret } from "xstate"

//Anime
import anime from "animejs/lib/anime.es.js"

// Vars
const button: HTMLElement = document.querySelector('#addNote')
const toggleService = interpret(toggleMachine)

const toggle = () => {
    button.addEventListener('click', () => {
        toggleService.send('TOGGLE');
    })
}

const buttonDisabled = (btnStatus : boolean) => {
    if(btnStatus) {
        button.setAttribute('disabled', '')
    } else {
        button.removeAttribute('disabled')
    }
}

const animate = (status: any) => {
    const tl = anime.timeline()
    if(status === 'active') {
        buttonDisabled(true);
        tl.add({
            targets: button,
            translateY: [0,-12,0],
            scale: [1,.85,1],
            rotate: 316,
            duration: 600,
            easing: "easeInOutSine"
        }).add({
            targets: '.note-selectors .first',
            translateY: [0, 80],
            duration: 3200,
            scaleY: [1.8, 1],
            },
            '-=400'
        ).add({
            targets: '.note-selectors .other',
            translateY: function(el){
                return [el.getAttribute('data-from'), el.getAttribute('data-to')]
            },
            duration: 1600,
            scaleY: [0, 1],
            opacity: {
                value: 1,
                duration: 10,
            },
            delay: anime.stagger(240), 
            complete: function(){
                buttonDisabled(false);
            }
            },
        '-=2600'
        );
    } else if (status === 'inactive') {
        buttonDisabled(true);
        tl.add({
            targets: button,
            rotate: 0,
            duration: 600,
            easing: 'easeInOutSine'
        }).add({
            targets: '.note-selectors .selector',
            translateY: function (el) {
                return [el.getAttribute('data-to'), 0]
            },
            duration: 400,
            delay: anime.stagger(60),
            easing: "easeInOutSine",
        },
        '-=400'
        )
    }
}

const init = () => {
    toggleService.onTransition((state) =>{
        console.log(state.value)
        animate(state.value);
    }).start();
    toggle();
}

init();
